doc.host-define:
{[copy-ca-cert]}
# Fix root tty RUN sed -i 's/^mesg n/tty -s \&\& mesg n/g' /root/.profile
# Install base packages (suppress dpkg interactive output) RUN export DEBIAN_FRONTEND=noninteractive && \ rm /etc/apt/apt.conf.d/70debconf && \ apt-get update && \ apt-get install -y --no-install-recommends sudo ssh wget vim gnupg lsb-release iputils-ping ca-certificates \ tzdata locales 2>&1
{[sudo-disable-core-dump]}
# Install CA certificate RUN update-ca-certificates
# Install PostgreSQL RUN RELEASE_CODENAME=`lsb_release -c | awk '{print $2}'` && \ echo 'deb http://apt.postgresql.org/pub/repos/apt/ '${RELEASE_CODENAME?}'-pgdg main' | \ tee -a /etc/apt/sources.list.d/pgdg.list && \ wget --quiet -O - https://www.postgresql.org/media/keys/ACCC4CF8.asc | sudo apt-key add - && \ apt-get update && \ apt-get install -y --no-install-recommends postgresql-common 2>&1 && \ sed -i 's/^\#create\_main\_cluster.*$/create\_main\_cluster \= false/' \ /etc/postgresql-common/createcluster.conf && \ apt-get install -y --no-install-recommends postgresql-{[pg-version]} postgresql-{[pg-version-upgrade]} 2>&1
# Create an ssh key for root so all hosts can ssh to each other as root RUN \ {[ssh-key-install]}
# Create common postgresql config RUN \ {[postgres-config-common-create]}
# Add doc user with sudo privileges RUN adduser --disabled-password --gecos "" {[host-user]} && \ echo '%{[host-user]} ALL=(ALL) NOPASSWD: ALL' >> /etc/sudoers
# Set UTF8 encoding RUN sed -i -e 's/# en_US.UTF-8 UTF-8/en_US.UTF-8 UTF-8/' /etc/locale.gen && \ dpkg-reconfigure --frontend=noninteractive locales && \ update-locale LANG=en_US.UTF-8 ENV LANG en_US.UTF-8
ENTRYPOINT service ssh restart && bash

execute.exe-cmd:
psql -c "
                        create or replace function create_test_table(prefix int, scale int, data bool) returns void as \$\$
                        declare
                            index int;
                        begin
                            for index in 1 .. scale loop
                                execute 'create table test_' || prefix || '_' || index || ' (id int)';

                                if data then
                                    execute 'insert into test_' || prefix || '_' || index || ' values (' || (prefix * index) || ')';
                                end if;
                            end loop;
                        end \$\$ LANGUAGE plpgsql;"

psql -c "
                        select pg_create_restore_point('generate WAL'); select {[pg-switch-wal]}();
                        select pg_create_restore_point('generate WAL'); select {[pg-switch-wal]}();"

psql -c "begin;
         create table important_table (message text);
         insert into important_table values ('{[test-table-data]}');
         commit;
         select * from important_table;"

psql -c "begin;
                                  drop table important_table;
                                  commit;
                                  select * from important_table;"

psql -c "begin;
                                  drop table important_table;
                                  commit;
                                  select * from important_table;"

psql -c "
         begin;
         create table replicated_table (message text);
         insert into replicated_table values ('{[test-table-data]}');
         commit;
         select * from replicated_table";

psql -c "
         begin;
         create table stream_table (message text);
         insert into stream_table values ('{[test-table-data]}');
         commit;
         select *, current_timestamp from stream_table";

psql -c "
    select pg_create_restore_point('test async push'); select {[pg-switch-wal]}();
    select pg_create_restore_point('test async push'); select {[pg-switch-wal]}();
    select pg_create_restore_point('test async push'); select {[pg-switch-wal]}();
    select pg_create_restore_point('test async push'); select {[pg-switch-wal]}();
    select pg_create_restore_point('test async push'); select {[pg-switch-wal]}();"

bash -c 'for i in {1..{[stress-scale-table]}};
    do psql -c "select create_test_table(${i?}, 1000, true)";
    done'
